import React, { Component } from 'react';
import axios from 'axios';

class App extends Component{

	constructor(props) {
	    super(props);
	    this.state = {
	    	searchResults: []
		}
    }

	showResults(response) {
		this.setState({
			searchResults: response.data.data
		})
	}

	search(url) {
		var _this = this
		axios.get(url).then(function (response) {
			console.log(response)
			_this.showResults(response)
		})
	}

	componentDidMount(){
	   this.search('http://localhost/medicamentos/search/ACEBROFILINA');
	}

    render(){
        return (
            <div className="container">
        		<h1>Pesquisa de Medicamentos</h1>
                <SearchBox />
                <Results searchResults={this.state.searchResults}/>
            </div>
        );
    }
}
class SearchBox extends Component{
    render(){
        return (
            <form>
  				<div className="form-group">
        			<input type="text" className="form-control" id="search" placeholder="Entre com um Nome, Laboratório ou Princípio Ativo" />
        		</div>
            	<input className="btn btn-primary" type="submit" />
            </form>
        );
    }
}
