import React, { Component } from 'react';
import axios from 'axios';

class App extends Component{

	constructor(props) {
	    super(props);
	    this.state = {
	    	searchResults: []
		}
		this.search = this.search.bind(this);
    }

	showResults(response) {
		this.setState({
			searchResults: response.data.data
		})
	}

	search(url) {
		axios.get(url).then(function (response) {
			this.showResults(response)
		}.bind(this))
	}

    render(){
        return (
            <div className="container">
        		<h2>Reserva de Medicamentos</h2>
                <SearchBox search={this.search} />
                <Results searchResults={this.state.searchResults}/>
            </div>
        );
    }
}

class SearchBox extends Component{
	constructor(props) {
		super(props)
		this.state = {value: ''};
		this.handleChange = this.handleChange.bind(this);
    	this.handleSubmit = this.handleSubmit.bind(this);
	}

    render(){
        return (
            <form onSubmit={this.handleSubmit}>
  				<div className="form-group">
        			<input type="text" className="form-control" ref="query" id="search" placeholder="Entre com um Nome, Laboratório ou Princípio Ativo" onChange={this.handleChange} />
        		</div>
            	<input className="btn btn-primary" type="submit" value="Pesquisar" />
            </form>
        );
    }

    handleChange(event) {
    	this.setState({value: event.target.value});
  	}

    handleSubmit(event) {
    	var url = `http://localhost/medicamentos/search/${this.state.value}`;
		this.props.search(url)
	    event.preventDefault();
	}
}

class Results extends Component{
    render(){
    	var resultItems = this.props.searchResults.map(function(result){
    		console.log(result)
    		return <ResultItem key={result.id} nome={result.nome} />
    	})
        return(
            <ul>
                {resultItems}
            </ul>           
        );
    }
}

class ResultItem extends Component{
    render(){
        return <li>{this.props.nome}</li>;
    }
}

export default App;
