import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

class App extends Component{

	constructor(props) {
	    super(props);
	    this.state = {
	    	searchResults: []
		}
    }

	showResults(response) {
		this.setState({
			searchResults: response.data.data
		})
	}

	search(url) {
		var _this = this
		axios.get(url).then(function (response) {
			_this.showResults(response)
		})
	}

	componentDidMount(){
	   this.search('ACEBROFILINA');
	}

    render(){
        return (
            <div className="container">
        		<h1>Pesquisa de Medicamentos</h1>
                <SearchBox search={this.search} />
                <Results searchResults={this.state.searchResults}/>
            </div>
        );
    }
}
class SearchBox extends Component{
	createAjax() {
		var query = ReactDOM.findDOMNode(this.refs.query).value
		var url = 'http://localhost/medicamentos/search/' + query
		this.props.search(query)
	}
    render(){
        return (
            <form>
  				<div className="form-group">
        			<input type="text" className="form-control" ref="query" id="search" placeholder="Entre com um Nome, Laboratório ou Princípio Ativo" />
        		</div>
            	<input className="btn btn-primary" type="button" value="Pesquisar" onClick={this.createAjax}/>
            </form>
        );
    }
}

class Results extends Component{
    render(){
    	var resultItems = this.props.searchResults.map(function(result){
    		console.log(result)
    		return <ResultItem key={result.id} nome={result.nome} />
    	})
        return(
            <ul>
                {resultItems}
            </ul>           
        );
    }
}

class ResultItem extends Component{
    render(){
        return <li>{this.props.nome}</li>;
    }
}

export default App;
