import React, { Component } from 'react';
import axios from 'axios';
import './App.css';

class App extends Component{

	constructor(props) {
	    super(props);
	    this.state = {
	    	searchResults: []
		}
		this.search = this.search.bind(this);
    }

	showResults(response) {
		this.setState({
			searchResults: response.data.data
		})
	}

	search(url) {
		axios.get(url).then(function (response) {
			this.showResults(response)
		}.bind(this))
	}

    render(){
        return (
            <div className="container">
        		<h2>Reserva de Medicamentos</h2>
                <SearchBox search={this.search} />
                <br/>
                <div className="clear">
                	<Results searchResults={this.state.searchResults}/>
                </div>
            </div>
        );
    }
}

class SearchBox extends Component{
	constructor(props) {
		super(props)
		this.state = {value: ''};
		this.handleChange = this.handleChange.bind(this);
    	this.handleSubmit = this.handleSubmit.bind(this);
	}

    render(){
        return (
            <form onSubmit={this.handleSubmit}>
  				<div className="form-group">
        			<input type="text" className="form-control" ref="query" id="search" placeholder="Entre com um Nome, Laboratório ou Princípio Ativo" onChange={this.handleChange} />
        		</div>
            	<input className="btn btn-primary" type="submit" value="Pesquisar" />
            </form>
        );
    }

    handleChange(event) {
    	this.setState({value: event.target.value});
  	}

    handleSubmit(event) {
    	var url = `http://localhost/medicamentos/search/${this.state.value}`;
		this.props.search(url)
	    event.preventDefault();
	}
}

class Results extends Component{
    render(){
    	var resultItems = this.props.searchResults.map(function(medicamento){
    		return <ResultItem key={medicamento.id} 
    						   nome={medicamento.nome}
    						   avatar={medicamento.avatar}
    						   laboratorio={medicamento.laboratorio}
    						   apresentacao={medicamento.apresentacao}
    						   valor_unitario={this.formatMoeda(medicamento.valor_unitario)}
    						   dropdown_options={this.getOptions(medicamento.estoque_inicial)}
    						   />
    	}.bind(this))
        return(
            <div className="row">
                {resultItems}
            </div>           
        );
    }

    formatMoeda(value) {
    	return 'R$ ' + value.toFixed(2).replace('.', ',');
    }

    getOptions(max) {
    	var options = []
    	for (var i = 1; i <= max; i++) {
    		options.push(i)
    	}
    	return options
    }

}

class ResultItem extends Component{
    render(){
        return(
        	<div className="col-md-3 line">
        		<div className="card h-100">
        			<img className="card-img-top" src={this.props.avatar} 
        			 alt={this.props.ggrem} />
				  	<div className="card-body h-100">
				    	<table className="table table-sm">
				    		<thead>
				    		<tr>
				    			<th colSpan="2">{this.props.nome}</th>
				    		</tr>
				    		</thead>
				    		<tbody>
				    		<tr>
				    			<th>Laboratório</th>
				    			<td>{this.props.laboratorio}</td>
				    		</tr>
				    		<tr>
				    			<th>Apresentação</th>
				    			<td>{this.props.apresentacao}</td>
				    		</tr>
				    		<tr>
				    			<th>Valor Unitário</th>
				    			<td>{this.props.valor_unitario}</td>
				    		</tr>
				    		</tbody>
				    	</table>
				    </div>
				    <div className="card-footer">
				    	<select className="form-control col-md-4">
				    		{this.props.dropdown_options.map((option) => <option key={option} value={option}>{option}</option>)}
				    	</select>
				    	<a href="#" className="btn btn-primary">Visitar</a>
				  	</div>
                </div>
        	</div>
        );
    }
}

export default App;
